-- ----------------------------
-- Procedure structure for TZ_UPDATE_HCL
-- ----------------------------
DROP PROCEDURE IF EXISTS `TZ_UPDATE_HCL`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `TZ_UPDATE_HCL`(IN userId INT,In contentId INT,IN ctype INT)
BEGIN
	/*点击数*/
	IF ctype = 1 THEN
			SET @count = (
				SELECT COUNT(1) FROM keke_hcl_detail WHERE user_id = userId AND content_id = contentId AND  type=ctype
				AND DATE_FORMAT(create_time,'%Y-%m-%d') = DATE_FORMAT(NOW(),'%Y-%m-%d')
			);
			IF @count=0 THEN
				UPDATE keke_content SET hits = IFNULL(hits,0) +1 WHERE id = contentId;
				SELECT 0;
			ELSE
				UPDATE keke_content SET hits = IFNULL(hits,0) +1 WHERE id = contentId;
				SELECT 1;
			END IF;
	/*收藏数*/	
	ELSEIF ctype = 2 THEN
			SET @count = (SELECT COUNT(1) FROM keke_hcl_detail WHERE user_id = userId AND content_id = contentId  AND  type=ctype );
			IF @count=0 THEN
				UPDATE keke_content SET collections = collections +1 WHERE id = contentId;
				SELECT 0;
			ELSE
				SET @z = (SELECT is_delete FROM keke_hcl_detail WHERE user_id = userId AND content_id = contentId  AND  type=ctype );
				IF @z=0 THEN
						UPDATE keke_hcl_detail SET is_delete = 1 WHERE   user_id = userId AND content_id = contentId  AND  type=ctype;
						UPDATE keke_content SET collections = IFNULL(collections,0) -1 WHERE id = contentId;
						SELECT 1;
				ELSE
						UPDATE keke_hcl_detail SET is_delete = 0 WHERE   user_id = userId AND content_id = contentId  AND  type=ctype;
						UPDATE keke_content SET collections = IFNULL(collections,0) +1 WHERE id = contentId;
						SELECT 0;
				END IF;
			END IF;
  /*喜欢数*/
	ELSEIF ctype = 3 THEN
			SET @count = (SELECT COUNT(1) FROM keke_hcl_detail WHERE user_id = userId AND content_id = contentId  AND  type=ctype );
			IF @count=0 THEN
				UPDATE keke_content SET loves = loves +1 WHERE id = contentId;
				SELECT 0;
			ELSE
				SET @z = (SELECT is_delete FROM keke_hcl_detail WHERE user_id = userId AND content_id = contentId  AND  type=ctype );
				IF @z=0 THEN
						UPDATE keke_hcl_detail SET is_delete = 1 WHERE   user_id = userId AND content_id = contentId  AND  type=ctype;
						UPDATE keke_content SET loves = IFNULL(loves,0) -1 WHERE id = contentId;
						SELECT 1;
				ELSE
						UPDATE keke_hcl_detail SET is_delete = 0 WHERE   user_id = userId AND content_id = contentId  AND  type=ctype;
						UPDATE keke_content SET loves = IFNULL(loves,0) +1 WHERE id = contentId;
						SELECT 0;
				END IF;
			END IF;
	END IF;
END
;;
DELIMITER ;
